chrome.runtime.onInstalled.addListener(o=>{o.reason==="install"&&console.log("vddgroupdial instalado con éxito."),chrome.contextMenus.create({id:"gsd-add-dial",title:"Añadir página a vddgroupdial",contexts:["page"]})});chrome.contextMenus.onClicked.addListener((o,e)=>{if(o.menuItemId==="gsd-add-dial"&&e?.url){const t=encodeURIComponent(e.url),d=encodeURIComponent(e.title||e.url);chrome.windows.create({url:`popup.html?url=${t}&title=${d}`,type:"popup",width:400,height:480})}});
